and c.brand_code=#{brandName, jdbcType=VARCHAR}
and c.catalog_code=#{catalogName, jdbcType=VARCHAR}
and c.model_code=#{modelName, jdbcType=VARCHAR}
and peoc.commodity_info_id=c.pk_id
and pe.delete_flag = '1' and pe.exchange_status = '1' and peoc.delete_flag = '1' and pe.last_up_time between #{start} and #{end} and pe.schema_name = peoc.schema_name
and pe.org_id in
#{org}
and pe.org_id =#{orgId}
and pe.schema_name = #{schemaName} and pe.pk_id = peoc.exchange_id group by pe.schema_name, pe.supplier_id, pe.org_id,
peoc.commodity_info_id,
