mapper.select:
select tt.schema_name,
t1.commodity_name commodityName, t1.commodity_code commodityCode, tt.commodity_info_id commodityId, (select t2.brand_name from c_commodity_brand t2 where t2.delete_flag=1 and
t1.schema_name=t2.schema_name and t2.brand_code=t1.brand_code) as brandName, (select t3.catalog_name from c_commodity_catalog t3 where t3.delete_flag=1 and
t1.schema_name=t3.schema_name and t3.catalog_code=t1.catalog_code) as catalogName, (select t4.model_name from c_commodity_model t4 where t4.delete_flag=1 and
t1.schema_name=t4.schema_name and t4.model_code=t1.model_code ) as modelName,
(select t5.org_name from c_sys_organization t5 where tt.org_id=t5.pk_id) as orgName, (select ps.supplier_name from purchase_supplier ps where ps.pk_id=tt.supplier_id) supplier, tt.supplier_id supplierId, tt.org_id orgId, tt.operation_type operationType, tt.schema_name schemaName from( select t11.schema_name, t11.supplier_id, t11.org_id, t11.operation_type
,t11.commodity_info_id
from( select schema_name, supplier_id, org_id, operation_type
,commodity_info_id
from ( select pwi.schema_name, pwi.supplier_id, pwi.org_id, pwi.operation_type
,wic.commodity_info_id
from purchase_warehouse_in pwi, urchase_warehouse_in_commodity wic where pwi.delete_flag = 1 and pwi.warehouse_in_status = 1 and pwi.schema_name = #{schemaName} and wic.delete_flag = 1 and substr(pwi.warehouse_in_date ,1,10) between #{start} and #{end}
and pwi.org_id in
#{org}
and pwi.org_id =#{orgId}
and pwi.schema_name = wic.schema_name and pwi.pk_id = wic.warehouse_in_id
group by pwi.schema_name, pwi.supplier_id, pwi.org_id,
wic.commodity_info_id,
pwi.operation_type union
select pr.schema_name, pr.supplier_id, pr.org_id, pr.operation_type
,prc.commodity_info_id
from purchase_refunds pr, purchase_refunds_commodity prc where pr.delete_flag = 1 and pr.refunds_status = 1 and prc.delete_flag = 1 and prc.schema_name = #{schemaName} and substr(pr.refunds_status,1,10) between #{start} and #{end}
and pr.org_id in
#{org}
and pr.org_id =#{orgId}
and pr.schema_name = prc.schema_name and pr.pk_id = prc.refunds_id group by pr.schema_name, pr.supplier_id, pr.org_id,
prc.commodity_info_id,
pr.operation_type union
select po.schema_name, po.supplier_id, po.purchase_org_id org_id, po.operation_type
,poc.commodity_info_id
from purchase_order po, purchase_order_commodity poc where po.delete_flag = 1 and po.purchase_status = 4 and substr(po.purchase_date,1,10) between #{start} and #{end}
and po.purchase_org_id in
#{org}
and po.purchase_org_id =#{orgId}
and poc.delete_flag = 1 and po.schema_name = #{schemaName} and po.schema_name = poc.schema_name and po.pk_id = poc.purchase_order_id group by po.schema_name, po.supplier_id, po.purchase_org_id,
poc.commodity_info_id,
po.operation_type union
select pe.schema_name, pe.supplier_id, pe.org_id, pe.operation_type
,peic.commodity_info_id
from purchase_exchange pe, purchase_exchange_in_commodity peic where pe.delete_flag = 1 and pe.exchange_status = 1 and peic.delete_flag = 1 and pe.schema_name = #{schemaName} and substr(pe.exchange_date,1,10)between #{start} and #{end}
and pe.org_id in
#{org}
and pe.org_id =#{orgId}
and pe.schema_name = peic.schema_name and pe.pk_id = peic.exchange_id group by pe.schema_name, pe.supplier_id, pe.org_id,
peic.commodity_info_id,
pe.operation_type union
select pe.schema_name, pe.supplier_id, pe.org_id, pe.operation_type
,peoc.commodity_info_id
from purchase_exchange pe, urchase_exchange_out_commodity peoc where pe.delete_flag = 1 and pe.exchange_status = 1 and peoc.delete_flag = 1 and pe.schema_name = #{schemaName} and substr(pe.exchange_date,1,10)between #{start} and #{end}
and pe.org_id in
#{org}
and pe.org_id =#{orgId}
and pe.schema_name = peoc.schema_name and pe.pk_id = peoc.exchange_id group by pe.schema_name, pe.supplier_id, pe.org_id,
peoc.commodity_info_id,
pe.operation_type) where 1=1
and supplier_id=#{supplierId}
and operation_type=#{operationType}
) t11 ) tt
,c_commodity t1 where t1.delete_flag = 1 and t1.schema_name = tt.schema_name and t1.pk_id = tt.commodity_info_id
and t1.commodity_code = '%'||#{commodityCode, jdbcType=VARCHAR}
and t1.commodity_name like '%'||#{commodityName, jdbcType=VARCHAR}||'%'
and t1.brand_code=#{brandName, jdbcType=VARCHAR}
and t1.catalog_code=#{catalogName, jdbcType=VARCHAR}
and t1.model_code=#{modelName, jdbcType=VARCHAR}

select t.model_code as modelCode,t.model_name as modelName from
c_commodity_model t where t.delete_flag=1 and t.status=2 and
t.schema_name=#{schemaName}

	and t.brand_code=#{brandCode}

select t.business_id as businessId,t.business_name as businessName
from finance_org_business_type t where t.delete_flag='1' and t.status='1'
and t.schema_name=#{schemaName}

	and t.org_id=#{orgId,jdbcType=VARCHAR}


	and t.business_type=#{businessType,jdbcType=VARCHAR}

select t.pk_id warehouseId,t.wh_name warehouseName from
storage_warehouse_info t where t.delete_flag=1 and t.wh_status=1
and t.org_id=#{orgId}
and t.schema_name=#{schemaName}

select tt2.commodityName commodityName, tt2.commodityCode commodityCode, tt2.org_id orgId, (select o.org_name from c_sys_organization o where o.pk_id=tt2.org_id)as orgName, tt2.commodity_id commodityId, tt2.catalogName catalogName, tt2.brandName brandName, tt2.modelName modelName, tt2.schema_name schemaName, tt2.warehouse_id whId, tt2.count1 inventoryNum, tt2.wh_code, tt2. wh_name whName, sew.safe_stock safeInventory,sew.unsalable_days unsalableDays from (select t1.commodity_name commodityName, t1.commodity_code commodityCode, t1.org_id, t1.pk_id commodity_id, (select t2.brand_name from c_commodity_brand t2 where t2.delete_flag=1 and
t1.schema_name=t2.schema_name and t2.brand_code=t1.brand_code) as brandName, (select t3.catalog_name from c_commodity_catalog t3 where t3.delete_flag=1 and
t1.schema_name=t3.schema_name and t3.catalog_code=t1.catalog_code) as catalogName, (select t4.model_name from c_commodity_model t4 where t4.delete_flag=1 and
t1.schema_name=t4.schema_name and t4.model_code=t1.model_code ) as modelName, tt1.schema_name,tt1.warehouse_id,tt1.count1, (select swi.wh_code from storage_warehouse_info swi where swi.pk_id=tt1.warehouse_id) as wh_code, (select swi.wh_name from storage_warehouse_info swi where swi.pk_id=tt1.warehouse_id) as wh_name
from (select ii.schema_name, ii.commodity_info_id,ii.warehouse_id,sum(ii.quantity_on_hand) count1 from inventory_item ii where ii.schema_name=#{schemaName} group by ii.schema_name, ii.commodity_info_id,ii.warehouse_id)tt1
, c_commodity t1 where 1=1
and t1.commodity_code like '%'||#{commodityCode, jdbcType=VARCHAR}||'%'
and t1.commodity_name like '%'||#{commodityName, jdbcType=VARCHAR}||'%'
and t1.brand_code=#{brandName, jdbcType=VARCHAR}
and t1.catalog_code=#{catalogName, jdbcType=VARCHAR}
and t1.model_code=#{modelName, jdbcType=VARCHAR}
and t1.org_id in
#{org,jdbcType=VARCHAR}
and t1.org_id = #{orgId,jdbcType=VARCHAR}
and tt1.warehouse_id=#{warehouseId,jdbcType=VARCHAR}
and tt1.commodity_info_id=t1.pk_id) tt2 , storage_early_warning sew where sew.commodity_code=tt2.commodityCode and sew.delete_flag=1 and sew.org_id=tt2.org_id and sew.safe_stock>tt2.count1
and sew.schema_name=tt2.schema_name and sew.wh_code=tt2.wh_code

select tt2.commodityName commodityName, tt2.commodityCode commodityCode, tt2.org_id orgId, (select o.org_name from c_sys_organization o where o.pk_id=tt2.org_id)as orgName, tt2.commodity_id commodityId, tt2.operation_type operationType, tt2.supplier_id supplierId, tt2.supplier_name supplier, tt2.catalogName catalogName, tt2.brandName brandName, tt2.modelName modelName, tt2.schema_name schemaName, tt2.warehouse_id whId, tt2.count1 inventoryNum, tt2.time1 inventoryInTime, tt2.wh_code, wh_name whName, tt2.infoContent infoContent, tt2.info_type infoType, sew.safe_stock safeInventory,sew.unsalable_days unsalableDays from (select t1.commodity_name commodityName, t1.commodity_code commodityCode, t1.org_id, t1.pk_id commodity_id, (select t2.brand_name from c_commodity_brand t2 where t2.delete_flag=1 and
t1.schema_name=t2.schema_name and t2.brand_code=t1.brand_code) as brandName, (select t3.catalog_name from c_commodity_catalog t3 where t3.delete_flag=1 and
t1.schema_name=t3.schema_name and t3.catalog_code=t1.catalog_code) as catalogName, (select t4.model_name from c_commodity_model t4 where t4.delete_flag=1 and
t1.schema_name=t4.schema_name and t4.model_code=t1.model_code ) as modelName, tt1.schema_name,tt1.warehouse_id,tt1.count1,tt1.time1,tt1.supplier_id,tt1.supplier_name,tt1.operation_type ,tt1.infoContent ,tt1.info_type, (select swi.wh_code from storage_warehouse_info swi where swi.pk_id=tt1.warehouse_id) as wh_code, (select swi.wh_name from storage_warehouse_info swi where swi.pk_id=tt1.warehouse_id) as wh_name from (select ii.schema_name, ii.commodity_info_id,ii.warehouse_id,decode(iii.info_type,'2',ii.supplier_id,null) supplier_id,decode(iii.info_type,'2',ii.supplier_name,null) supplier_name, decode(iii.info_type,'2',ii.operation_type,null) operation_type ,decode(iii.info_type,'2',iii.info_content,null) infoContent,iii.info_type,sum(iii.quantity) count1, min(iii.create_time) time1 from inventory_item ii,inventory_item_info iii where ii.delete_flag=1
and ii.schema_name = #{schemaName} and ii.pk_id=iii.inventory_item_id group by ii.schema_name, ii.commodity_info_id,ii.warehouse_id,decode(iii.info_type,'2',ii.supplier_id,null),decode(iii.info_type,'2',ii.supplier_name,null),decode(iii.info_type,'2',ii.operation_type,null) ,decode(iii.info_type,'2',iii.info_content,null) ,iii.info_type)tt1 , c_commodity t1 where 1=1
and t1.commodity_code like '%'||#{commodityCode, jdbcType=VARCHAR}||'%'
and t1.commodity_name like '%'||#{commodityName, jdbcType=VARCHAR}||'%'
and t1.brand_code=#{brandName, jdbcType=VARCHAR}
and t1.catalog_code=#{catalogName, jdbcType=VARCHAR}
and t1.model_code=#{modelName, jdbcType=VARCHAR}
and t1.org_id in
#{org}
and t1.org_id = #{orgId}
and tt1.warehouse_id=#{warehouseId}
and tt1.commodity_info_id=t1.pk_id) tt2 , storage_early_warning sew where sew.commodity_code=tt2.commodityCode and sew.delete_flag=1 and to_char(sysdate-sew.unsalable_days,'yyyy/MM/dd')> tt2.time1 and sew.org_id=tt2.org_id and sew.schema_name=tt2.schema_name and sew.wh_code=tt2.wh_code

select t.commodity_code commodityCode,t.org_id orgId, (select cs.spec_name from c_commodity_spec cs where cs.delete_flag=1 and cs.schema_name=t.schema_name and cs.status=2 and cs.spec_code=t.spec_code) as specName, (select csi.spec_item_name from c_commodity_spec_item csi where csi.delete_flag=1 and csi.schema_name=t.schema_name and csi.spec_item_code=t.spec_item_code) as specItem from c_commodity2spec t where t.delete_flag=1 and t.commodity_code in
#{item}
and t.schema_name=#{schemaName}

select po.schema_name schemaName, po.supplier_id supplierId, po.purchase_org_id orgId, po.operation_type operationType,
poc.commodity_info_id commodityId,
nvl((sum(poc.order_quantity)),'0') as purchaseCount, nvl(sum(poc.order_total_amount),'0') as purchaseAmount from purchase_order po, purchase_order_commodity poc where po.delete_flag = 1 and po.purchase_status in (1,3,4) and substr(po.purchase_date,1,10) between #{start} and #{end} and poc.delete_flag = 1 and po.schema_name = poc.schema_name and po.schema_name=#{schemaName}
and po.purchase_org_id in
#{org}
and po.purchase_org_id =#{orgId}
and po.pk_id = poc.purchase_order_id group by po.schema_name, po.supplier_id, po.purchase_org_id,
poc.commodity_info_id,
po.operation_type
